#ifndef CAN_STRUCTS_H_
#define CAN_STRUCTS_H_

#include <stdint.h>

// Heartbeats
// Safety Module
typedef struct SMHeartbeat_t {
	uint8_t state;
	uint8_t vbatt;
	uint8_t targetState;
} SMHeartbeat_t;

// Central Controller
typedef struct CCHeartbeat_t {
	uint8_t state;
	uint8_t vbatt;
} CCHeartbeat_t;

// Front Sensor Module
typedef struct FSMHeartbeat_t {
	uint8_t state;
	uint8_t vbatt;
	uint8_t requestedTorque;
} FSMHeartbeat_t;

// Rear Sensor Module
typedef struct RSMHeartbeat_t {
	uint8_t state;
	uint8_t vbatt;
	uint8_t fanCurrent;
	uint8_t leftPumpStatus;
	uint8_t rightPumpStatus;
} RSMHeartbeat_t;

// Driver Interface Module
typedef struct DIMHeartbeat_t {
	uint8_t state;
	uint8_t vbatt;
	uint8_t requestedState;
} DIMHeartbeat_t;

// Accumulator Fan Controller
typedef struct AFCHeartbeat_t {
	uint8_t state;
	uint8_t vbatt;
	uint8_t fan1Current;
	uint8_t fan2Current;
	uint8_t fan3Current;
} AFCHeartbeat_t;

// Telemetry Module
typedef struct TMHeartbeat_t {
	uint8_t state;
	uint8_t vbatt;
} TMHeartbeat_t;


#endif /* CAN_STRUCTS_H_ */